      return self._vk.call('messages.getLongPollServer', self.config.userConfig.forGetLongPollServer).then(vkr => {
        self.config.longpollServer = vkr.server;
        self.config.longpollTs = vkr.ts;
        self.config.longpollKey = vkr.key;
