          }
        }

        self._vk.debug(EVENT_RESPONSE_TYPE, {
          body: res,
          section: 'longpoll'
        });

        self._debug({
          type: 'pollResponse',
          data: res
        });

        let vkr = res;

        if (vkr.ts) {
          if (vkr.ts) {
            self.config.longpollTs = vkr.ts;
          }

          if (vkr.updates && vkr.updates.length) {
