              if (captchaUrl) {
                captchaUrl = `${configuration.PROTOCOL}://${configuration.BASE_DOMAIN}${captchaUrl[0]}`;
                let captchaSid = captchaUrl.match(/sid=([0-9]+)/);

                if (captchaSid) {
