                    checkCodeURL = checkCodeURL ? checkCodeURL[4] : '';
                    checkCodeURL = `${configuration.PROTOCOL}://${configuration.MOBILE_SUBDOMAIN}.${configuration.BASE_DOMAIN}${checkCodeURL}`;
                    err.checkCodeUrl = checkCodeURL;
